"use server";

import { generateObject } from "ai";
import { google } from "@ai-sdk/google";

import { db } from "@/firebase/admin";
import { feedbackSchema } from "@/constants";

export async function createFeedback(params: CreateFeedbackParams) {
  const { interviewId, userId, transcript, feedbackId } = params;

  try {
    const formattedTranscript = transcript
      .map(
        (sentence: { role: string; content: string }) =>
          `- ${sentence.role}: ${sentence.content}\n`
      )
      .join("");

    const { object } = await generateObject({
      model: google("gemini-2.0-flash-001", {
        structuredOutputs: false,
      }),
      schema: feedbackSchema,
      prompt: `
        You are an AI interviewer analyzing a mock interview. Your task is to evaluate the candidate based on structured categories. Be thorough and detailed in your analysis. Don't be lenient with the candidate. If there are mistakes or areas for improvement, point them out.
        Transcript:
        ${formattedTranscript}

        Please score the candidate from 0 to 100 in the following areas. Do not add categories other than the ones provided:
        - **Communication Skills**: Clarity, articulation, structured responses.
        - **Technical Knowledge**: Understanding of key concepts for the role.
        - **Problem-Solving**: Ability to analyze problems and propose solutions.
        - **Cultural & Role Fit**: Alignment with company values and job role.
        - **Confidence & Clarity**: Confidence in responses, engagement, and clarity.
        `,
      system:
        "You are a professional interviewer analyzing a mock interview. Your task is to evaluate the candidate based on structured categories",
    });

    const feedback = {
      interviewId: interviewId,
      userId: userId,
      totalScore: object.totalScore,
      categoryScores: object.categoryScores,
      strengths: object.strengths,
      areasForImprovement: object.areasForImprovement,
      finalAssessment: object.finalAssessment,
      createdAt: new Date().toISOString(),
    };

    let feedbackRef;

    if (!db) {
      console.error("Database not configured");
      return { success: false };
    }

    if (feedbackId) {
      feedbackRef = db.collection("feedback").doc(feedbackId);
    } else {
      feedbackRef = db.collection("feedback").doc();
    }

    await feedbackRef.set(feedback);

    return { success: true, feedbackId: feedbackRef.id };
  } catch (error) {
    console.error("Error saving feedback:", error);
    return { success: false };
  }
}

export async function getInterviewById(id: string): Promise<Interview | null> {
  if (!db) {
    console.error("Database not configured");
    return null;
  }

  const interview = await db.collection("interviews").doc(id).get();

  return interview.data() as Interview | null;
}

export async function getFeedbackByInterviewId(
  params: GetFeedbackByInterviewIdParams
): Promise<Feedback | null> {
  const { interviewId, userId } = params;

  if (!db) {
    console.error("Database not configured");
    return null;
  }

  const querySnapshot = await db
    .collection("feedback")
    .where("interviewId", "==", interviewId)
    .where("userId", "==", userId)
    .limit(1)
    .get();

  if (querySnapshot.empty) return null;

  const feedbackDoc = querySnapshot.docs[0];
  return { id: feedbackDoc.id, ...feedbackDoc.data() } as Feedback;
}

export async function getLatestInterviews(
  params: GetLatestInterviewsParams
): Promise<Interview[] | null> {
  const { userId, limit = 20 } = params;

  if (!db) {
    console.error("Database not configured");
    return null;
  }

  try {
    // Try the optimal query first (requires composite index)
    const interviews = await db
      .collection("interviews")
      .orderBy("createdAt", "desc")
      .where("finalized", "==", true)
      .where("userId", "!=", userId)
      .limit(limit)
      .get();

    return interviews.docs.map((doc) => ({
      id: doc.id,
      ...doc.data(),
    })) as Interview[];

  } catch (error: any) {
    // If index is missing, fall back to simpler query and filter client-side
    if (error.code === 9 || error.message?.includes('index')) {
      console.warn("Firestore index missing for getLatestInterviews, using fallback");
      try {
        // Simpler query: just get finalized interviews and filter/sort client-side
        const interviews = await db
          .collection("interviews")
          .where("finalized", "==", true)
          .limit(limit * 3) // Get more to account for filtering out current user
          .get();

        const interviewData = interviews.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        })) as Interview[];

        // Filter out current user's interviews and sort by createdAt desc
        const filteredInterviews = interviewData
          .filter((interview) => interview.userId !== userId)
          .sort((a, b) => {
            const dateA = new Date(a.createdAt || 0).getTime();
            const dateB = new Date(b.createdAt || 0).getTime();
            return dateB - dateA; // desc order
          })
          .slice(0, limit); // Apply limit after sorting

        return filteredInterviews;

      } catch (fallbackError) {
        console.error("Error in fallback query for getLatestInterviews:", fallbackError);
        return []; // Return empty array instead of null to prevent crashes
      }
    }

    console.error("Error fetching latest interviews:", error);
    return []; // Return empty array instead of null
  }
}

export async function getInterviewsByUserId(
  userId: string
): Promise<Interview[] | null> {
  if (!db) {
    console.error("Database not configured");
    return null;
  }

  try {
    // Try the optimal query first (requires composite index)
    const interviews = await db
      .collection("interviews")
      .where("userId", "==", userId)
      .orderBy("createdAt", "desc")
      .get();

    return interviews.docs.map((doc) => ({
      id: doc.id,
      ...doc.data(),
    })) as Interview[];

  } catch (error: any) {
    // If index is missing, fall back to simpler query and sort client-side
    if (error.code === 9 || error.message?.includes('index')) {
      console.warn("Firestore index missing, using fallback query");
      try {
        const interviews = await db
          .collection("interviews")
          .where("userId", "==", userId)
          .get();

        const interviewData = interviews.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        })) as Interview[];

        // Sort client-side by createdAt desc
        return interviewData.sort((a, b) => {
          const dateA = new Date(a.createdAt || 0).getTime();
          const dateB = new Date(b.createdAt || 0).getTime();
          return dateB - dateA; // desc order
        });

      } catch (fallbackError) {
        console.error("Error in fallback query:", fallbackError);
        return []; // Return empty array instead of null to prevent crashes
      }
    }

    console.error("Error fetching interviews:", error);
    return []; // Return empty array instead of null
  }
}

lib/actions/auth.action.ts.content:
"use server";

import { auth, db } from "@/firebase/admin";
import { cookies } from "next/headers";

// Session duration (1 week)
const SESSION_DURATION = 60 * 60 * 24 * 7;

// Set session cookie
export async function setSessionCookie(idToken: string) {
  const cookieStore = await cookies();

  if (!auth) {
    throw new Error("Authentication not configured");
  }

  // Create session cookie
  const sessionCookie = await auth.createSessionCookie(idToken, {
    expiresIn: SESSION_DURATION * 1000, // milliseconds
  });

  // Set cookie in the browser
  cookieStore.set("session", sessionCookie, {
    maxAge: SESSION_DURATION,
    httpOnly: true,
    secure: process.env.NODE_ENV === "production",
    path: "/",
    sameSite: "lax",
  });
}

export async function signUp(params: SignUpParams) {
  const { uid, name, email } = params;

  try {
    if (!db) {
      return {
        success: false,
        message: "Database not configured. Please set up Firebase.",
      };
    }

    // check if user exists in db
    const userRecord = await db.collection("users").doc(uid).get();
    if (userRecord.exists)
      return {
        success: false,
        message: "User already exists. Please sign in.",
      };

    // save user to db
    await db.collection("users").doc(uid).set({
      name,
      email,
      // profileURL,
      // resumeURL,
    });

    return {
      success: true,
      message: "Account created successfully. Please sign in.",
    };
  } catch (error: any) {
    console.error("Error creating user:", error);

    // Handle Firebase specific errors
    if (error.code === "auth/email-already-exists") {
      return {
        success: false,
        message: "This email is already in use",
      };
    }

    return {
      success: false,
      message: "Failed to create account. Please try again.",
    };
  }
}

export async function signIn(params: SignInParams) {
  const { email, idToken } = params;

  try {
    if (!auth) {
      return {
        success: false,
        message: "Authentication not configured. Please set up Firebase.",
      };
    }

    const userRecord = await auth.getUserByEmail(email);
    if (!userRecord)
      return {
        success: false,
        message: "User does not exist. Create an account.",
      };

    await setSessionCookie(idToken);

    return {
      success: true,
      message: "Signed in successfully.",
    };
  } catch (error: any) {
    console.log("");

    return {
      success: false,
      message: "Failed to log into account. Please try again.",
    };
  }
}

// Sign out user by clearing the session cookie
export async function signOut() {
  const cookieStore = await cookies();

  cookieStore.delete("session");
}

// Get current user from session cookie
export async function getCurrentUser(): Promise<User | null> {
  const cookieStore = await cookies();

  const sessionCookie = cookieStore.get("session")?.value;
  if (!sessionCookie) return null;

  try {
    if (!auth) {
      console.log("Auth not configured");
      return null;
    }

    const decodedClaims = await auth.verifySessionCookie(sessionCookie, true);

    // Try to get user info from Firestore database
    if (db) {
      try {
        const userRecord = await db
          .collection("users")
          .doc(decodedClaims.uid)
          .get();

        if (userRecord.exists) {
          return {
            ...userRecord.data(),
            id: userRecord.id,
          } as User;
        }
      } catch (dbError: any) {
        console.warn("Firestore not available:", dbError.message);
        // Continue to fallback below
      }
    }

    // Fallback: Return basic user info from session token when Firestore is unavailable
    console.log("Using fallback authentication (Firestore unavailable)");
    return {
      id: decodedClaims.uid,
      name: decodedClaims.name || decodedClaims.email?.split('@')[0] || "User",
      email: decodedClaims.email || "",
    } as User;

  } catch (error: any) {
    console.log("Session verification failed:", error.message);
    // Invalid or expired session
    return null;
  }
}

// Check if user is authenticated
export async function isAuthenticated() {
  const user = await getCurrentUser();
  return !!user;
}

